import React from 'react';
import './AboutUs.css';
import logo from './logo.png';
import { useEffect } from 'react'; 

const AboutUs = () => {

  useEffect(() => {
    // Function to handle smooth scrolling
    const handleSmoothScroll = (event) => {
      event.preventDefault();
      const targetId = event.currentTarget.getAttribute('href').substring(1);
      const targetElement = document.getElementById(targetId);
      if (targetElement) {
        targetElement.scrollIntoView({
          behavior: 'smooth',
          block: 'start'
        });
      }
    };

    // Attach event listener to all anchor links
    const links = document.querySelectorAll('.nav-link');
    links.forEach(link => link.addEventListener('click', handleSmoothScroll));

    // Clean up event listeners on component unmount
    return () => {
      links.forEach(link => link.removeEventListener('click', handleSmoothScroll));
    };
  }, []); // Empty dependency array ensures this effect runs once

  useEffect(() => {
    // Function to handle scroll-to-top button visibility
    const handleScroll = () => {
      const scrollTopButton = document.querySelector('.scroll-to-top');
      if (window.scrollY > 200) {
        scrollTopButton.classList.add('show');
      } else {
        scrollTopButton.classList.remove('show');
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Clean up event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Empty dependency array ensures this effect runs once

  return (
    <div className="about-us">
      <div className="logo-container">
        <img src={logo} alt="Hotel Reservations App" className="home-logo" />
      </div>
      <br /><br />
      <section className="hero-section">
        <h1>About Us</h1>
        <p>Discover our journey and what drives us.</p>
      </section>

      <section id="our-story" className="our-story">
        <h2>Our Story</h2>
        <p>
          Established in 2024, CheckInn was born from a passion for travel and hospitality. Our mission is to make hotel booking seamless and enjoyable for everyone.
          We understand that finding the perfect place to stay can sometimes be overwhelming, so we've created a platform that simplifies the process. At CheckInn, we bring together a wide range of accommodations, from luxury resorts to budget-friendly options, ensuring that every traveler can find the perfect match for their needs and preferences.
          Our user-friendly interface allows you to explore, compare, and book hotels with ease, offering transparent pricing and personalized recommendations. We are committed to providing exceptional customer service, and our team is always ready to assist you with any questions or special requests.
          Whether you're planning a family vacation, a business trip, or a quick weekend getaway, CheckInn is your trusted partner in finding the best accommodations. With a focus on quality, reliability, and convenience, we aim to elevate your travel experience, making it more enjoyable and stress-free.
          Join us on your next adventure and discover the world with CheckInn, where your comfort and satisfaction are our top priorities.
        </p>
      </section>

      <section id="our-values" className="our-values">
        <h2>Our Values</h2>
        <div className="values-container">
          <div className="value-item">
            <i className="fas fa-users"></i>
            <h2>üë•</h2>
            <h3>Customer First</h3>
            <p>We prioritize our customers‚Äô needs above all else.</p>
          </div>
          <div className="value-item">
            <i className="fas fa-globe"></i>
            <h2>üåç</h2>
            <h3>Innovation</h3>
            <p>We constantly innovate to enhance your experience.</p>
          </div>
          <div className="value-item">
            <i className="fas fa-heart"></i>
            <h2>‚ù§Ô∏è</h2>
            <h3>Passion</h3>
            <p>Our team is passionate about hospitality and service.</p>
          </div>
        </div>
      </section>

      <section id="team-section" className="team-section">
        <h2>Meet Our Team</h2>
        <div className="team-container">
          <div className="team-member">
            <img src="path-to-image" alt="Team Member" />
            <h3>Anja Stevkovska</h3>
            <p>Founder & CEO</p>
            <p>Anja is dedicated to providing an exceptional travel experience through innovative solutions and customer-focused services. With a background in computer science and a passion for design, Anja leads CheckInn with a vision of excellence.</p>
          </div>
          {/* Add more team members as needed */}
          <div className="team-member">
            <img src="path-to-image" alt="Team Member" />
            <h3>John Doe</h3>
            <p>CTO</p>
            <p>John brings extensive experience in technology and software development to the team. His expertise ensures that CheckInn‚Äôs platform is cutting-edge and user-friendly.</p>
          </div>
          <div className="team-member">
            <img src="path-to-image" alt="Team Member" />
            <h3>Jane Smith</h3>
            <p>Head of Customer Service</p>
            <p>Jane is dedicated to delivering exceptional customer service. Her role involves ensuring that every guest has a smooth and satisfying experience.</p>
          </div>
        </div>
      </section>

      <footer className="about-footer">
        <p>&copy; 2024 CheckInn. All rights reserved.</p>
        <p>Website by <a href="#">Anja Stevkovska</a></p>
      </footer>

      <button className="scroll-to-top" onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}>
        ‚Üë
      </button>
    </div>
  );
};

export default AboutUs;
