import React, { useState } from 'react';
import './Settings.css';

const Settings = () => {
    const [isModalVisible, setIsModalVisible] = useState(null);
    const [settings, setSettings] = useState({
        changePassword: false,
        privacySettings: false,
        notificationPreferences: false,
        twoFactorAuth: false,
        accountActivity: false,
    });

    const openModal = (modalType) => {
        setIsModalVisible(modalType);
    };

    const closeModal = () => {
        setIsModalVisible(null);
    };

    const handleCheckboxChange = (event) => {
        const { name, checked } = event.target;
        setSettings(prevSettings => ({
            ...prevSettings,
            [name]: checked
        }));
    };

    const handleEnableTwoFactorAuth = () => {
        alert("Two-Factor Authentication Enabled");
    };

    const handleViewAccountActivity = () => {
        alert("Viewing Account Activity");
    };

    return (
        <div className="settings-container">
            <h1>Settings</h1>
            <div className="settings-section">
                <h2>Account Settings</h2>
                <div className="setting-option">
                    <label>Change Password</label>
                    <button className="change-btn" onClick={() => openModal('changePassword')}>Change</button>
                </div>
                <div className="setting-option">
                    <label>Privacy Settings</label>
                    <button className="edit-btn" onClick={() => openModal('privacySettings')}>Edit</button>
                </div>
                <div className="setting-option">
                    <label>Notification Preferences</label>
                    <button className="edit-btn" onClick={() => openModal('notificationPreferences')}>Edit</button>
                </div>
            </div>

            <div className="settings-section">
                <h2>App Preferences</h2>
                <div className="setting-option">
                    <label>Language</label>
                    <select>
                        <option value="en">English</option>
                        <option value="mk">Macedonian</option>
                        <option value="es">Spanish</option>
                    </select>
                </div>
                <div className="setting-option">
                    <label>Theme</label>
                    <select>
                        <option value="light">Light</option>
                        <option value="dark">Dark</option>
                    </select>
                </div>
            </div>

            <div className="settings-section">
                <h2>Security</h2>
                <div className="setting-option">
                    <label>Two-Factor Authentication</label>
                    <button className="enable-btn" onClick={handleEnableTwoFactorAuth}>Enable</button>
                </div>
                <div className="setting-option">
                    <label>Account Activity</label>
                    <button className="view-btn" onClick={handleViewAccountActivity}>View</button>
                </div>
            </div>

            {/* Modals */}
            {isModalVisible === 'changePassword' && (
                <div className="modal show">
                    <div className="modal-content">
                        <h2>Change Password</h2>
                        <form>
                            <label>
                                New Password:
                                <input type="password" />
                            </label>
                            <button className="save-btn" onClick={closeModal}>Save Changes</button>
                            <button className="cancel-btn" onClick={closeModal}>Cancel</button>
                        </form>
                    </div>
                </div>
            )}

            {isModalVisible === 'privacySettings' && (
                <div className="modal show">
                    <div className="modal-content">
                        <h2>Privacy Settings</h2>
                        <form>
                            <div className="setting-option">
                                <label>
                                    <input
                                        type="checkbox"
                                        name="privacySettings"
                                        checked={settings.privacySettings}
                                        onChange={handleCheckboxChange}
                                    />
                                    Enable Privacy Settings
                                </label>
                            </div>
                            <button className="save-btn" onClick={closeModal}>Save Changes</button>
                            <button className="cancel-btn" onClick={closeModal}>Cancel</button>
                        </form>
                    </div>
                </div>
            )}

            {isModalVisible === 'notificationPreferences' && (
                <div className="modal show">
                    <div className="modal-content">
                        <h2>Notification Preferences</h2>
                        <form>
                            <div className="setting-option">
                                <label>
                                    <input
                                        type="checkbox"
                                        name="notificationPreferences"
                                        checked={settings.notificationPreferences}
                                        onChange={handleCheckboxChange}
                                    />
                                    Receive Notifications
                                </label>
                            </div>
                            <button className="save-btn" onClick={closeModal}>Save Changes</button>
                            <button className="cancel-btn" onClick={closeModal}>Cancel</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Settings;
