import React from 'react';
import './AboutUs.css';
import logo from './logo.png';
import { useEffect } from 'react'; 
import { Link, useNavigate } from 'react-router-dom';

const AboutUs = () => {

  <div class="profile-icons">
  <a href="/profile" class="profile-icon">
