import React from 'react';
import './ExploreHotels.css';

function ExploreHotels() {
  const hotels = [
    { id: 1, name: 'Hotel Sunshine', location: 'Miami, FL', imageUrl: 'hotel1.jpg' },
    { id: 2, name: 'Mountain View Resort', location: 'Aspen, CO', imageUrl: 'hotel2.jpg' },
    { id: 3, name: 'Beachside Inn', location: 'Malibu, CA', imageUrl: 'hotel3.jpg' },
    { id: 4, name: 'Urban Retreat', location: 'New York, NY', imageUrl: 'hotel4.jpg' },
    // Add more hotel data as needed
  ];

  return (
    <div className="explore-hotels">
      <h1>Explore Hotels</h1>
      <p>Discover the best hotels in various destinations. Choose your perfect stay!</p>

      <div className="hotels-grid">
        {hotels.map((hotel) => (
          <div key={hotel.id} className="hotel-card">
            <img src={hotel.imageUrl} alt={hotel.name} className="hotel-image" />
            <div className="hotel-info">
              <h3>{hotel.name}</h3>
              <p>{hotel.location}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ExploreHotels;
