import React, { useState } from 'react';
import './ExploreHotels.css';

function ExploreHotels() {
  const [searchTerm, setSearchTerm] = useState('');

  const hotels = [
    { id: 1, name: 'Hotel Sunshine', location: 'Miami, FL', imageUrl: 'pictures/download (6).jpg' },
    { id: 2, name: 'Mountain View Resort', location: 'Aspen, CO', imageUrl: 'pictures/download (6).jpg' },
    { id: 3, name: 'Beachside Inn', location: 'Malibu, CA', imageUrl: 'pictures/download (6).jpg' },
    { id: 4, name: 'Urban Oasis', location: 'New York, NY', imageUrl: 'pictures/download (6).jpg' },
    { id: 5, name: 'Desert Mirage', location: 'Phoenix, AZ', imageUrl: 'pictures/download (6).jpg' },
  ];

  const filteredHotels = hotels.filter(hotel =>
    hotel.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    hotel.location.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="explore-hotels">
      <h1>Explore Hotels</h1>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Search hotels..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button>Search</button>
      </div>

      <div className="hotel-grid">
        {filteredHotels.map(hotel => (
          <div key={hotel.id} className="hotel-card">
            <img src={hotel.imageUrl} alt={hotel.name} />
            <div className="hotel-info">
              <h3>{hotel.name}</h3>
              <p>{hotel.location}</p>
              <button>View Details</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ExploreHotels;
