import React, { useState } from 'react';
import './ExploreHotels.css';
import { FaStar, FaMapMarkerAlt, FaFilter, FaSearch } from 'react-icons/fa';
import logo from './logo.png'; 


function ExploreHotels() {
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('recommended');
  const [filters, setFilters] = useState({
    priceRange: '',
    starRating: '',
    amenities: [],
  });

  const hotels = [
    {
      id: 1,
      name: 'Hotel Sunshine',
      location: 'Miami, FL',
      price: 120,
      rating: 4.5,
      distance: 0.5,
      amenities: ['Free Wi-Fi', 'Pool', 'Spa'],
      imageUrl: 'pictures/download (6).jpg',
    },
    {
      id: 2,
      name: 'Mountain View Resort',
      location: 'Aspen, CO',
      price: 250,
      rating: 4.8,
      distance: 2.0,
      amenities: ['Breakfast Included', 'Skiing', 'Hot Tub'],
      imageUrl: 'pictures/download (7).jpg',
    },
    {
      id: 3,
      name: 'Beachside Inn',
      location: 'Malibu, CA',
      price: 180,
      rating: 4.2,
      distance: 1.2,
      amenities: ['Ocean View', 'Free Parking', 'Pet Friendly'],
      imageUrl: 'pictures/download (8).jpg',
    },
    {
      id: 4,
      name: 'Urban Oasis',
      location: 'New York, NY',
      price: 220,
      rating: 4.7,
      distance: 0.8,
      amenities: ['Rooftop Bar', 'Gym', 'Free Wi-Fi'],
      imageUrl: 'pictures/download (9).jpg',
    },
    {
      id: 5,
      name: 'Desert Mirage',
      location: 'Phoenix, AZ',
      price: 150,
      rating: 4.3,
      distance: 3.5,
      amenities: ['Pool', 'Golf Course', 'Spa'],
      imageUrl: 'pictures/download (10).jpg',
    },
    {
      id: 6,
      name: 'Hotel Sunshine',
      location: 'Miami, FL',
      price: 120,
      rating: 4.5,
      distance: 0.5,
      amenities: ['Free Wi-Fi', 'Pool', 'Spa'],
      imageUrl: 'pictures/download (6).jpg',
    },
    {
      id: 7,
      name: 'Mountain View Resort',
      location: 'Aspen, CO',
      price: 250,
      rating: 4.8,
      distance: 2.0,
      amenities: ['Breakfast Included', 'Skiing', 'Hot Tub'],
      imageUrl: 'pictures/download (7).jpg',
    },
    {
      id: 8,
      name: 'Beachside Inn',
      location: 'Malibu, CA',
      price: 180,
      rating: 4.2,
      distance: 1.2,
      amenities: ['Ocean View', 'Free Parking', 'Pet Friendly'],
      imageUrl: 'pictures/download (8).jpg',
    },
    {
      id: 9,
      name: 'Urban Oasis',
      location: 'New York, NY',
      price: 220,
      rating: 4.7,
      distance: 0.8,
      amenities: ['Rooftop Bar', 'Gym', 'Free Wi-Fi'],
      imageUrl: 'pictures/download (9).jpg',
    },
   
   
  ];

  
  const filteredHotels = hotels
    .filter((hotel) => {
      return (
        hotel.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        hotel.location.toLowerCase().includes(searchTerm.toLowerCase())
      );
    })
    .filter((hotel) => {
      // Price Range Filter
      if (filters.priceRange) {
        const [minPrice, maxPrice] = filters.priceRange.split('-').map(Number);
        if (hotel.price < minPrice || hotel.price > maxPrice) return false;
      }
      // Star Rating Filter
      if (filters.starRating) {
        if (Math.floor(hotel.rating) !== Number(filters.starRating)) return false;
      }
      // Amenities Filter
      if (filters.amenities.length > 0) {
        return filters.amenities.every((amenity) => hotel.amenities.includes(amenity));
      }
      return true;
    });

  // Sorting Hotels based on selected option
  const sortedHotels = filteredHotels.sort((a, b) => {
    switch (sortOption) {
      case 'priceLowHigh':
        return a.price - b.price;
      case 'priceHighLow':
        return b.price - a.price;
      case 'ratingHighLow':
        return b.rating - a.rating;
      case 'distance':
        return a.distance - b.distance;
      case 'nameAZ':
        return a.name.localeCompare(b.name);
      default:
        return a.id - b.id; 
    }
  });


  const handleFilterChange = (e) => {
    const { name, value, checked } = e.target;
    if (name === 'amenities') {
      setFilters((prevFilters) => {
        if (checked) {
          return { ...prevFilters, amenities: [...prevFilters.amenities, value] };
        } else {
          return {
            ...prevFilters,
            amenities: prevFilters.amenities.filter((amenity) => amenity !== value),
          };
        }
      });
    } else {
      setFilters((prevFilters) => ({
        ...prevFilters,
        [name]: value,
      }));
    }
  };

  return (
    <div className="explore-hotels">
      <div className="logo-container">
      <img src={logo} alt="Hotel Reservations App" className="home-logo" />
      </div>
      <div className="hero-section">
        <h1>Discover Your Perfect Stay</h1>
        <p>Find and book the best hotels around the world at unbeatable prices.</p>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search by hotel name or location"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button>
            <FaSearch />
          </button>
        </div>
      </div>

      <div className="main-content">
        
        <div className="filters-sidebar">
          <h2>
            <FaFilter /> Filters
          </h2>
          <div className="filter-group">
            <h3>Price Range</h3>
            <select name="priceRange" onChange={handleFilterChange}>
              <option value="">Any</option>
              <option value="50-100">$50 - $100</option>
              <option value="101-200">$101 - $200</option>
              <option value="201-300">$201 - $300</option>
              <option value="301-500">$301 - $500</option>
            </select>
          </div>

          <div className="filter-group">
            <h3>Star Rating</h3>
            <select name="starRating" onChange={handleFilterChange}>
              <option value="">Any</option>
              <option value="5">5 Stars</option>
              <option value="4">4 Stars</option>
              <option value="3">3 Stars</option>
              <option value="2">2 Stars</option>
              <option value="1">1 Star</option>
            </select>
          </div>

          <div className="filter-group">
            <h3>Amenities</h3>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="Free Wi-Fi"
                onChange={handleFilterChange}
              />
              Free Wi-Fi
            </label>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="Pool"
                onChange={handleFilterChange}
              />
              Pool
            </label>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="Spa"
                onChange={handleFilterChange}
              />
              Spa
            </label>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="Breakfast Included"
                onChange={handleFilterChange}
              />
              Breakfast Included
            </label>
            <label>
              <input
                type="checkbox"
                name="amenities"
                value="Free Parking"
                onChange={handleFilterChange}
              />
              Free Parking
            </label>
          
          </div>

          <div className="filter-group">
  <h3>Room Type</h3>
  <label>
    <input
      type="checkbox"
      name="roomType"
      value="Single"
      onChange={handleFilterChange}
    />
    Single Room
  </label>
  <label>
    <input
      type="checkbox"
      name="roomType"
      value="Double"
      onChange={handleFilterChange}
    />
    Double Room
  </label>
  <label>
    <input
      type="checkbox"
      name="roomType"
      value="Suite"
      onChange={handleFilterChange}
    />
    Suite
  </label>
</div>

<div className="filter-group">
  <h3>Distance to City Center</h3>
  <select name="distance" onChange={handleFilterChange}>
    <option value="">Any</option>
    <option value="0-1">0 - 1 km</option>
    <option value="1-5">1 - 5 km</option>
    <option value="5-10">5 - 10 km</option>
    <option value="10-20">10 - 20 km</option>
  </select>
</div>

<div className="filter-group">
  <h3>Guest Rating</h3>
  <select name="guestRating" onChange={handleFilterChange}>
    <option value="">Any</option>
    <option value="9-10">9 - 10</option>
    <option value="8-9">8 - 9</option>
    <option value="7-8">7 - 8</option>
    <option value="6-7">6 - 7</option>
  </select>
</div>
        </div>

        
        <div className="hotels-section">
          <div className="sorting-bar">
            <label htmlFor="sortOptions">Sort by:</label>
            <select
              id="sortOptions"
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
            >
              <option value="recommended">Recommended</option>
              <option value="priceLowHigh">Price: Low to High</option>
              <option value="priceHighLow">Price: High to Low</option>
              <option value="ratingHighLow">Rating: High to Low</option>
              <option value="distance">Distance</option>
              <option value="nameAZ">Name: A to Z</option>
            </select>
          </div>

          <div className="hotel-grid">
            {sortedHotels.length > 0 ? (
              sortedHotels.map((hotel) => (
                <div key={hotel.id} className="hotel-card">
                  <img src={hotel.imageUrl} alt={hotel.name} />
                  <div className="hotel-info">
                    <h3>{hotel.name}</h3>
                    <p className="location">
                      <FaMapMarkerAlt /> {hotel.location}
                    </p>
                    <p className="price">${hotel.price} / night</p>
                    <p className="rating">
                      <FaStar /> {hotel.rating}
                    </p>
                    <div className="amenities">
                      {hotel.amenities.map((amenity, index) => (
                        <span key={index}>{amenity}</span>
                      ))}
                    </div>
                    <button className="details-button">View Details</button>
                  </div>
                </div>
              ))
            ) : (
              <p className="no-results">No hotels match your search criteria.</p>
            )}
          </div>
        </div>
      </div>
      <div className="pagination">
    <button className="prev-page">&laquo; Prev</button>
    <span className="page-number">1</span>
    <span className="page-number">2</span>
    <span className="page-number">3</span>
    <span className="page-number">...</span>
    <span className="page-number">10</span>
    <button className="next-page">Next &raquo;</button>
  </div>
    </div>
  );


}

export default ExploreHotels;
